<template lang="pug">
nav.global-nav
  .-menu(v-if='!isGlobal')
    #nav.-slide
      ul.-main-menu
        li.-items
          a.-link(href='/')
            span.menu-service-kokunai.icon_svg
            span.-text 国内航空券
        li.-items
          a.-link(href='/tour')
            span.menu-service-tour.icon_svg
            span.-text 国内ツアー
          span.-goto GoTo対象
        li.-items
          a.-link(href='/hotel/')
            span.menu-service-hotel.icon_svg
            span.-text ホテル
          span.-goto GoTo対象
        li.-items(:class='{ "is-active": cssControll }')
          nuxt-link.-link(to='/takeout/')
            template(v-if='cssControll')
              span.menu-service-gourmet.icon_svg
            template(v-else)
              span.menu-service-gourmet-blue.icon_svg
            span.-text グルメ
          span.-new NEW!
        li.-items
          a.-link(href='/rentacar/')
            span.menu-service-rentacar.icon_svg
            span.-text レンタカー
        li.-items.is-large
          a.-link(href='/dp/')
            span.menu-service-dp-kokunai.icon_svg
            span.-text 国内航空券<br>＋ホテル
          span.-goto GoTo対象
        li.-items.is-large
          a.-link(href='/tour/train')
            span.menu-service-tour-train.icon_svg
            span.-text 新幹線<br>＋ホテル
          span.-goto GoTo対象
        li.-items.bus
          a.-link(href='/bus/')
            span.menu-service-bus.icon_svg
            span.-text 高速バス
        li.-items
          a.-link(href='/ferry/')
            span.menu-service-ferry.icon_svg
            span.-text フェリー
        li.-items
          a.-link(href='/international-flights/')
            span.menu-service-kaigai.icon_svg
            span.-text 海外航空券
        li.-items.is-large
          a.-link(href='/dp/international')
            span.menu-service-dp-kaigai.icon_svg
            span.-text 海外航空券<br>＋ホテル
        li.-items.guide
          a.-link(href='/guide/')
            span.menu-service-guide.icon_svg
            span.-text 観光ガイド
        li.-items.other(:class='{ "is-active": isShowOther }')
          button.-button(
            ref='buttonOther',
            @mousedown.prevent='toggleOtherMenu',
            @touchstart.prevent='toggleOtherMenu'
          )
            span.menu-service-other_on.icon_svg(v-if='isShowOther')
            span.menu-service-other.icon_svg(v-else)
            span.-text その他
            next.-icon
      no-ssr
        template(v-if='$window.width > 767')
          ul.-sub-menu.is-takeout(v-show='isShowCars', v-cloak)
            li.-items
              a.-link(href='/rentacar/') 国内レンタカー
                next.-icon
            li.-items
              a.-link(href='/kaigai') 海外レンタカー
                next.-icon
            li.-items
              a.-link(href='/') グルメ
                next.-icon
      template
        ul.-sub-menu.is-other(v-show='isShowOther', v-cloak)
          li.-items
            a.-link(href='/wifi/') WiFiレンタル
              next.-icon
          li.-items
            a.-link(href='/insurance/') 旅行保険
              next.-icon
          li.-items
            a.-link(href='/premium/') プレミアム
              next.-icon
  .-menu(v-else)
    ul.-main-menu.is-global
      li.-items(
        v-for='(menu, key) in navMenu.en.list',
        :key='key',
        :class='{ "is-active": menu.active }'
      )
        nuxt-link.-link(:to='menu.link')
          span.icon_svg(:class='menu.icon')
          span.-text {{ menu.title }}
        span.-new(v-if='menu.new') NEW
</template>

<script lang="ts">
import { Component, Vue } from 'nuxt-property-decorator'
import Next from '~/assets/icon/next.svg'

@Component({
  components: {
    Next
  }
})
export default class Navigation extends Vue {
  mounted() {
    const elMenuList = document.getElementById('nav')
    if (elMenuList) {
      const vwSize = elMenuList.scrollWidth / 25
      elMenuList.scrollLeft = vwSize * 2.4
      this.cssControll = true
    }
  }

  get urlTo() {
    return process.env.PATH
  }

  public isShowOther = false
  public isShowCars = false
  cssControll = false

  toggleOtherMenu() {
    this.isShowOther = !this.isShowOther
    if (this.isShowOther) {
      const buttonOther = this.$refs.buttonOther
      if (!(buttonOther instanceof HTMLElement)) {
        return
      }
      buttonOther.focus()
    }
  }

  closeOtherMenu() {
    this.isShowOther = false
  }

  toggleCarsMenu() {
    this.isShowCars = !this.isShowCars
    if (this.isShowCars) {
      const buttonCars = this.$refs.buttonCars
      if (!(buttonCars instanceof HTMLElement)) {
        return
      }
      buttonCars.focus()
    }
  }

  closeCarsMenu() {
    this.isShowCars = false
  }

  // 仮フラグ TODO:: 正規の仕様に差し替え
  public isApp = false
  public isGlobal = false
  public navMenu = {
    en: {
      list: [
        {
          icon: 'menu-service-kokunai',
          title: 'Frights',
          link: '/fright/',
          new: false,
          active: false
        },
        {
          icon: 'menu-service-cars',
          title: 'Rent a car',
          link: '/',
          new: true,
          active: true
        },
        {
          icon: 'menu-service-guide',
          title: 'Travel Guide',
          link: '/guide/',
          new: false,
          active: false
        }
      ]
    }
  }

  // public readonly $t = this.$t.bind(this)
  // public navListData = this.setData('nav_list')
  // public subNavListData = this.setData('sub_nav_list')
  // public show = false
  // showAsideNavigation() {
  //   this.show = !this.show
  // }
  // private setData(text) {
  //   return this.$t(text)
  // }
}
</script>

