$menu_width: 25vw
.global-nav
  position: relative
  width: 100%
  background-color: $white
  button
    cursor: pointer
    border: none
    &:focus
      outline: none
  .-menu
    position: relative
  .-main-menu
    width: 100%
    &.is-global
      .-link
        width: auto
    .-items
      &.is-active
        background-color: $sub_color
        .-link,
        .-button
          background-color: $sub_color
          color: $white
          .-icon,
          .-text
            color: $white
  .-sub-menu
    position: absolute
    width: 200px
    background-color: $white
    border: 1px solid $light_gray
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, .4)
    z-index: 2
    &::before
      content: ''
      display: block
      position: absolute
    .-items
      &:not(:last-of-type)
        border-bottom: 1px solid $light_gray
    .-link
      display: block
      position: relative
      height: 40px
      line-height: 40px
      padding: 0 16px
      font-size: 12px
      text-decoration: none
      &:hover
        background: $light_gray
    .-icon
      position: absolute
      top: 50%
      right: 12px
      width: 14px
      height: 14px
      margin-top: -7px
      padding-right: 5px
      speak: none
  @media screen and (max-width: 767px)
    .is-pc,
    br
      display: none
    .-menu
      border-bottom: 1px solid $sub_color
    .-slide
      width: 100%
      overflow-x: scroll
      overflow-y: hidden
    .-main-menu
      display: flex
      flex-wrap: nowrap
      align-items: stretch
      justify-content: space-between
      box-pack: justify
      transition-duration: .5s
      transition-timing-function: ease-out
      .-items
        flex: 1 1 auto
        text-align: center
        transform: translateZ(0)
        .-link
          border-right: 1px solid #afc9ed
        &.bus,
        &.guide
          .icon_svg
            width: 23px
        &.other
          .icon_svg
            padding-top: 15px
        &.is-large
          .-link
            margin: 7px 0
            width: $menu_width * 1.5
          .icon_svg
            width: 73px
      .-link,
      .-button
        display: block
        position: relative
        width: $menu_width
        height: 44px
        line-height: .8em
        margin: 7px 0
        color: $sub_color
        background-color: $white
        font-size: 10px
        text-align: center
        text-decoration: none
        .-icon
          display: none
        .icon_svg
          background-repeat: no-repeat
          display: inline-block
          height: 26px
          width: 26px
        .-text
          display: block
          position: absolute
          top: 83%
          width: 90%
          left: 50%
          letter-spacing: .1px
          line-height: 1em
          text-align: center
          transform: translate(-50%, -50%)
          font-size: 10px
          color: #1c5db5
      .-new
        position: absolute
        top: 0
        left: 0
        width: 31px
        height: 32px
        background: url('~@/static/img/top/new.png') left top no-repeat
        background-size: contain
        color: transparent
      .-goto
        position: absolute
        top: 0
        left: 0
        width: 31px
        height: 32px
        background: url('~@/static/img/top/goto.png') left top no-repeat
        background-size: contain
        color: transparent
    .-sub-menu
      top: 69px
      right: 5px
      &::before
        top: -12px
        right: 5vw
        border: 8px solid transparent
        border-bottom-color: $sub_color
        transform: rotate(180deg)
  @media screen and (min-width: 768px)
    background-color: $light_gray
    .-menu
      background-color: $light_gray
      width: $container_max_width_pc
      margin: 0 auto
    .icon_svg
      display: none
    .-main-menu
      display: table
      &.is-global
        table-layout: fixed
      .-items
        position: relative
        display: table-cell
        vertical-align: middle
        &.is-sp
          display: none
        &.is-large
          .-link
            line-height: 1.5
          .-text
            line-height: 1
      .-link,
      .-button
        display: block
        font-size: $font_size_tiny
        width: 100%
        height: 40px
        line-height: 40px
        padding: 2px 4px
        text-align: center
        letter-spacing: normal
        background-color: $light_gray
        &:hover,
        &:focus
          background-color: $sub_color
          .-text
            color: $white
          .-icon
            fill: $white
        .-icon
          display: inline-block
          transform: rotate(90deg)
          width: 12px
          height: 12px
          margin-left: 8px
      .-text
        font-size: $font_size_tiny
      .-new
        position: absolute
        top: -16px
        display: block
        background-color: $accent_color
        font-size: 10px
        width: 95%
        line-height: 10px
        text-align: center
        color: $white
        padding: 3px 0
        z-index: 0
        &::after
          content: ""
          display: block
          position: absolute
          left: 0
          bottom: -8px
          width: 0
          height: 0
          margin-left: 0
          border-style: solid
          border-width: 0 8px 8px 0
          border-color: transparent $accent_color transparent transparent
          z-index: -1
      .-goto
        position: absolute
        top: -16px
        display: block
        background-color: $accent_color
        font-size: 10px
        width: 95%
        line-height: 10px
        text-align: center
        color: $white
        padding: 3px 0
        z-index: 0
        &::after
          content: ""
          display: block
          position: absolute
          left: 0
          bottom: -8px
          width: 0
          height: 0
          margin-left: 0
          border-style: solid
          border-width: 0 8px 8px 0
          border-color: transparent $accent_color transparent transparent
          z-index: -1
    .-sub-menu
      top: 48px
      &.is-other
        right: 8px
      &.is-takeout
        left: 400px
      &::before
        top: -19px
        right: 25px
        border: 10px solid transparent
        border-bottom-color: $white
</style>
